import UIKit

class HomeMovieAppRouter: HomeMovieAppRouterProtocol {
    var navigation: UINavigationController?
//    static var mainStoryboard: UIStoryboard {
//    return UIStoryboard(name: "HomeMovieAppStoryboard", bundle: Bundle.main)
//    }

    static func createModule(navigation:UINavigationController) -> UIViewController {

        let view = HomeMovieAppViewController()
        let presenter = HomeMovieAppPresenter()
        let interactor = HomeMovieAppInteractor()
        let router = HomeMovieAppRouter()
        
        view.presenter = presenter
        presenter.interactor = interactor
        presenter.router = router
        presenter.view = view
        interactor.presenter = presenter
        router.navigation = navigation
        
        return view
    }
    
    func nextViewNavigation() {

    }
}
